import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Header from '../components/Header';
import { fetchQuestions } from '../redux/actions';

class Quiz extends React.Component {
  constructor() {
    super();
    this.state = {
      questions:[],
    };
  }

  componentDidMount() {
    const { fetch, token, questions } = this.props;
    fetch(token);
    this.setState({ questions: questions.results });
  }

  render() {
    const { questions } = this.props;
    console.log(questions);
    return (
      <div>
        <Header />
        <div>
          { questions.payload.results.map((quest, index) => (
            <p key={ index }>{ quest }</p>
          ))}
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  token: state.token,
  questions: state.trivia.payload,
});

const mapDispatchToProps = (dispatch) => ({
  fetch: (token) => dispatch(fetchQuestions(token)),
});

Quiz.propTypes = ({
  fetch: PropTypes.func.isRequired,
  token: PropTypes.string.isRequired,
  questions: PropTypes.objectOf(PropTypes.any).isRequired,
});

export default connect(mapStateToProps, mapDispatchToProps)(Quiz);
