import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchToken, addNewPlayer } from '../redux/actions';
import './Login.css';

class Login extends Component {
  constructor() {
    super();
    this.state = {
      isButtonDisabled: true,
      name: '',
      email: '',
    };
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value }, this.enableButton);
  }

  enableButton = () => {
    const { name, email } = this.state;
    const isNameValid = name.length > 0;
    const isEmailValid = email.length > 0;
    this.setState({ isButtonDisabled: !(isNameValid && isEmailValid) });
  }

  handleClick = async () => {
    const { dispatchUser, requestToken, history } = this.props;
    const { name, email } = this.state;
    await requestToken();
    dispatchUser(name, email);
    history.push('/quiz');
  }

  render() {
    const { name, email, isButtonDisabled } = this.state;
    return (
      <form className="form">
        <label htmlFor="name">
          Nome:
          <input
            className="input-login"
            type="text"
            data-testid="input-player-name"
            id="name"
            name="name"
            value={ name }
            onChange={ this.handleChange }
          />
        </label>
        <label htmlFor="email">
          Email:
          <input
            className="input-login"
            type="text"
            data-testid="input-gravatar-email"
            id="email"
            name="email"
            value={ email }
            onChange={ this.handleChange }
          />
        </label>
        <button
          className="btn-play"
          data-testid="btn-play"
          type="button"
          disabled={ isButtonDisabled }
          onClick={ this.handleClick }
        >
          Play
        </button>
        <Link to="/configuracoes">
          <button
            className="btn-config"
            type="button"
            data-testid="btn-settings"
          >
            Configurações
          </button>
        </Link>
      </form>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  requestToken: () => dispatch(fetchToken()),
  dispatchUser: (name, email) => dispatch(addNewPlayer(name, email)),
});

Login.propTypes = ({
  requestToken: PropTypes.func.isRequired,
  dispatchUser: PropTypes.func.isRequired,
  history: PropTypes.objectOf(PropTypes.any).isRequired,
});

export default connect(null, mapDispatchToProps)(Login);
