import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import Header from '../components/Header';
import './Feedback.css';
import tristeza from '../assets/tristeza.gif';
import happy from '../assets/happy.gif';

class Feedback extends Component {
  render() {
    const { assertions, score } = this.props;
    const auxNumber = 3;
    return (
      <div>
        <Header />
        <main className="main-feedback">
          <div className="feedback-container">
            <h1
              data-testid="feedback-text"
            >
              {assertions >= auxNumber ? 'Well Done!' : 'Could be better...'}
            </h1>
            {assertions >= auxNumber
              ? <img alt="" src="shorturl.at/akHMR" />
              : <img alt="" src="shorturl.at/abAKQ" />}
            <p data-testid="feedback-total-score">{`‚≠ê Score: ${score}`}</p>
            <p data-testid="feedback-total-question">{`‚úÖ Corrects: ${assertions}`}</p>
          </div>
          <section className="btn-container">
            <Link to="/">
              <button
                type="button"
                className="btn-feedback"
                data-testid="btn-play-again"
              >
                üéÆ Play Again
              </button>
            </Link>
            <Link to="/ranking">
              <button
                type="button"
                className="btn-feedback"
                data-testid="btn-ranking"
              >
                üèÜ Ranking
              </button>
            </Link>
          </section>
        </main>
      </div>
    );
  }
}

Feedback.propTypes = ({
  assertions: PropTypes.number.isRequired,
  score: PropTypes.number.isRequired,
});

const mapStateToProps = (state) => ({
  assertions: state.player.assertions,
  score: state.player.score,
});

export default connect(mapStateToProps, null)(Feedback);
