import { GET_TOKEN, REQUEST_TOKEN } from '../actions';

const INITIAL_STATE = '';

function token(state = INITIAL_STATE, action) {
  switch (action.type) {
  case GET_TOKEN:
    return action.payload;
  case REQUEST_TOKEN:
    return
  default:
    return state;
  }
}

export default token;
